export default function headerFixed(){let e=document.querySelector("#site-header"),t=window.pageYOffset;window.addEventListener("scroll",(function(){let d=window.pageYOffset;d<=t?(e.classList.add("relative","bg-transparent"),e.classList.remove("fixed","bg-bgHeaderFixed","top-0","left-0","right-0","animate-slide-down")):(e.classList.remove("relative","bg-transparent"),e.classList.add("fixed","bg-bgHeaderFixed","top-0","left-0","right-0","animate-slide-down")),t=d}))}document.addEventListener("DOMContentLoaded",(function(){headerFixed()}));